import React from "react";
import Elevators from "./Elevators";
class Floors extends React.Component {
    constructor(props) {
        super(props);
        for(let i = 0; i < this.state.floors; i++){
            copyArray[i] = "Call";
        }
        this.state = {
            page: 0,
            floors: 10,
            floorStatus: copyArray,
            speed: 1,
            elevatorsPlaces: []
        };
    }

    callElevator = (floor) => {
        fetch("http://localhost/Building/elevatorCallApi", {
            method: "POST",
            withCredentials: true,
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            credentials: "include",
            body: JSON.stringify({
                floor:floor
            }),
        }).then((response) => response.json())
            .then((result) => {
                let arrayCopy = this.state.elevatorsPlaces.slice();
                arrayCopy[result.elevator] = result.distance;
                this.setState({
                    elevatorsPlaces:arrayCopy
                    });
            });
    }

    componentDidMount = () => {
        this.checkElevatorPlace();
    };

    checkElevatorPlace = () => {
        fetch("http://localhost/Building/elevatorsPlaces", {
            method: "POST",
            credentials: 'include',
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
            }),
        }).then((response) => response.json())
            .then((result) => {
                var copyArray = [];
                this.setState({
                    elevatorsPlaces: result,
                    page:1
                });
            });
    }

    render() {
        if (!this.state.page) {
            return <div className="Loading"></div>;
        }
        var floorDetails = [];
        for (var i = this.state.floors - 1; i >= 0; i--) {
            let floor = i;
            floorDetails.push(
                <div className={`floorSection floorBlock_${i}`}>
                    <div className="floorNum">{floor}</div>
                    <Elevators floor={i} elevatorsPlace={this.state.elevatorsPlaces} />
                    <div className="elevatorCall" name="elevatorCall" onClick={() => this.callElevator(floor)}>{this.state.floorStatus[i]}</div>
                </div>
            );
        }
        return (
            <div className="floorsWrapper">
                {floorDetails}
            </div>
        );
    }
}

export default Floors;
