import React from "react";
import Elevators from "./Elevators";
class Floors extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            page: 0,
            floors: 10,
            floorStatus: [],
            elevatorsPlaces: [],
            speed: 1,
        };
    }

    callElevator = (e, floor) => {
        fetch("http://localhost/Building/elevatorCallApi", {
            method: "POST",
            withCredentials: true,
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            credentials: "include",
            body: JSON.stringify({
                floor: floor
            }),
        }).then((response) => response.json())
            .then((result) => {
                let arrayCopyStatus = this.state.floorStatus.slice();
                arrayCopyStatus[floor] = "waiting";
                this.setState({
                    floorStatus: arrayCopyStatus
                });
                var start = this.state.elevatorsPlaces[result.elevator];
                var end = floor;
                if (start < end) {
                    console.log(result);
                    this.moveElevatorUp(result, start, end);
                } else {
                    this.moveElevatorDown(result, start, end);
                }
                setTimeout(
                    () => {
                        this.updateElevatorStatus(result.elevator);
                        arrayCopyStatus[floor] = "arrived";
                        this.setState({
                            floorStatus: arrayCopyStatus
                        });
                        arrayCopyStatus[floor] = "Call";
                        setTimeout(
                            () => {
                                this.setState({
                                    floorStatus: arrayCopyStatus
                                });
                            }, 2000);
                    }, (result.distance + 1) * 1000);
            });
    }

    moveElevatorUp = (result, start, end) => {
        console.log(result);
        var count = 0;
        for (var i = start; i <= end; i++) {
            let arrayCopy = this.state.elevatorsPlaces.slice();
            arrayCopy[result.elevator] = i;
            setTimeout(
                () => {
                    this.moveElevetorBySpeed(arrayCopy);
                }, count * 1000);
            count++;
        }
    }
    moveElevatorDown = (result, start, end) => {
        var count = 0;
        for (var i = start; i >= end;) {
            let arrayCopy = this.state.elevatorsPlaces.slice();
            arrayCopy[result.elevator] = i;
            setTimeout(
                () => {
                    this.moveElevetorBySpeed(arrayCopy);
                }, count * 1000);
            count++;
            i--;
        }
    }

    moveElevetorBySpeed = (arrayCopy) => {
        this.setState({
            elevatorsPlaces: arrayCopy,
        });
    }

    updateElevatorStatus = (elevator) => {

        fetch("http://localhost/Building/updateElevatorStatus", {
            method: "POST",
            credentials: 'include',
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                elevatorNum: elevator
            }),
        }).then((response) => response.json())
            .then((result) => {
            });
    }

    componentDidMount = () => {
        this.checkElevatorPlace();
        var copyArray = [];
        for (let i = 0; i < this.state.floors; i++) {
            copyArray[i] = "Call";
        }
        this.setState({
            floorStatus: copyArray,
        });
    };

    checkElevatorPlace = () => {
        fetch("http://localhost/Building/elevatorsPlaces", {
            method: "POST",
            credentials: 'include',
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
            }),
        }).then((response) => response.json())
            .then((result) => {
                this.setState({
                    elevatorsPlaces: result,
                    page: 1
                });
            });
    }

    render() {
        if (!this.state.page) {
            return <div className="Loading"></div>;
        }

        var floorDetails = [];
        for (var i = this.state.floors - 1; i >= 0; i--) {
            var elevatorCall = "free";
            if (this.state.floorStatus[i] === "waiting" || this.state.floorStatus[i] === "arrived") {
                elevatorCall = "Occupied";
            }
            let floor = i;
            floorDetails.push(
                <div className={`floorSection floorBlock_${i}`}>
                    <div className="floorNum">{floor}</div>
                    <Elevators floor={i} elevatorsPlace={this.state.elevatorsPlaces} />
                    <div className={`elevatorCall ${elevatorCall}`} name="elevatorCall" onClick={(e) => this.callElevator(e, floor)}>{this.state.floorStatus[i]}</div>
                </div>
            );
        }
        return (
            <div className="floorsWrapper">
                {floorDetails}
            </div>
        );
    }
}

export default Floors;
